<?phpnamespace Controller;use Service\Mongo;class ListController extends ApiController{    public function indexAction()    {        $query  = [];        $limit  = 20;        $offset = 0;        $order  = ['external_id' => 1];        /** @var \MongoDB\Driver\Cursor $result */        $result = Mongo::get()->findWithCount($query, [            'limit' => $limit,            'skip' => $offset,            'sort' => $order,            'projection' => [               '_id' => 0            ]        ]);        return $result;    }}