<?phpnamespace Controller;use Service\Mongo;class ShowController extends ApiController{    public function indexAction($id)    {        /** @var \MongoDB\Driver\Cursor $result */        $result = Mongo::get()->findById($id, [            'limit' => 1,            'skip' => 0,            'projection' => [                '_id' => 0            ]        ]);        return $result;    }}